INSERT INTO `help_comment` VALUES (1, '3791862e-fd29-46f2-9690-93c49e8caeaf', 1648248808398868480, '真不错', 0, '2023-04-20 17:22:19', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (1, '近日，由浙江省民政厅、省经信厅、省商务厅主办，以“共富先行浙里康养”为主题的“2023浙江（国际）康养产业博览会”开幕，全方位展现最新的养老领域发展成果，促进康养产业高质量发展。', '2023-04-17 20:31:07', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (2, '近日，第二届娄底市养老护理职业技能竞赛暨娄底市三、四星级养老机构等级评定颁奖仪式举行。按照《湖南省养老机构等级评定管理办法（试行）》（湘民发〔2020〕31号）和《娄底市养老机构等级评定实施细则（试行）》等有关规定，经市养老机构等级评定委员会办公室审定，最终，评选出四星级养老机构4家、三星级养老机构6家。（查看名单详情←）此次养老护理职业技能竞赛，来自全市的12支队伍48名选手参加了此次竞赛。最终决出一等奖1名、二等奖2名、三等奖5名，并评选出2个优秀组织奖。其中彭玲荣获竞赛个人一等奖，被授予“娄底市技术能手”和“娄底市巾帼建功标兵”荣誉称号，还将被授予“娄底市五一劳动奖章”。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (3, '近日，从杭州市民政局获悉，杭州市政府批复同意《杭州市养老服务设施布局专项规划》，规划明确了近期至2025年、远期至2035年的养老服务设施规划目标、配建指标、配置指引等。规划总体范围为杭州市域16850平方千米。重点范围包括上城、拱墅、西湖、滨江、萧山、余杭、临平、钱塘、富阳、临安十个城区以及三县市中心城区。规划重点目标包括床位目标、用地目标等。规划至2025年，在加强管理、优化服务、着力提高现有床位利用率的基础上，稳步推进床位增长，每万名常住老人养老机构床位不低于300张；远期至2035年，力争每万名常住老人养老机构床位不低于310张。人均养老设施用地不低于0.25平方米。规划明确，构建“街道乡镇—社区村”两个层级组成的居家养老服务网络，实现城乡社区村居家养老服务中心全覆盖。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (4, '近日，丹阳市民政局为18家养老机构颁发等级认定牌匾。据悉，丹凤温馨园被江苏省民政厅评为五级养老机构，是镇江市唯一一家；丹阳善德养老发展有限公司被镇江市民政局评为四级养老机构；丹阳市老年康复中心等9家机构被评为三级养老机构；丹阳曙光老年康复中心等7家机构被评为二级养老机构。据统计，截至2022年底，丹阳市共有60周岁以上老年人23.68万人，占户籍人口总数的29.99%，老龄化程度居全省前列，已迈入中高度老龄化社会。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (5, '近日，从广州市南沙区民政局获悉，南沙区于2023年4月7日正式印发并实施《南沙区公办养老机构入住本区港澳老年人实施方案》（下称《实施方案》），向港澳老年人开放南沙区公办养老机构床位。首批50张专属养老床位设置在南沙区养老院，服务好、价格平，助力南沙打造港澳居民“心归处”。如何申请入住在广州市南沙区内工作（连续6个月以上）的港澳人士及其配偶的直系亲属，或已在广州市南沙区内常住（连续6个月以上）的港澳老年人即可向南沙区民政局申请入住轮候。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (6, '日前，辽宁省19个部门联合印发《关于深入推进医养结合发展的实施意见》。近年来，辽宁省医养结合工作不断推进，共有214家医养结合机构，床位共有5.2万余张，养老机构与医疗卫生机构开展签约服务，签约率达100%。《意见》聚焦积极应对人口老龄化国家战略，从居家医疗、社区服务、机构服务、服务衔接、支持政策、队伍建设、服务监管等7个方面提出20项具体措施。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (7, '4月14日，太仓市举办第四届养老护理员技能竞赛。全市10余家养老服务机构共30余名选手参赛。竞赛以《养老护理员国家职业技能标准（2019年版）》三级/高级工及以上知识和技能要求为基础，主要考核养老护理员对《标准》知识要求的理解掌握，以及养老护理员针对老年人身体、精神状况提供生活照护、基础照护、康复照护、心理支持等服务的实操能力。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (8, '4月13日，江苏省民政厅、拉萨市民政局召开对口支援工作座谈会，交流两地民政事业发展情况。会议强调，下一步两地民政部门要进一步加强对口支援合作，深化人才培训、养老服务、社会救助、社会组织和社工站建设等交流交融，共同推进民政事业高质量发展。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (9, '近日，西藏自治区民政厅联合政法委、文明办、教育厅等10部门印发《关于开展特殊困难老年人探访关爱服务的实施方案》，提出到2023年底前，基本建立特殊困难老年人探访关爱服务机制；到2024年底，探访关爱服务普遍有效开展；到2025年底，确保特殊困难老年人月探访率达到100%，失能老年人能够得到有效帮扶，探访关爱服务机制更加健全。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (10, '近日，甘肃省民政厅表示，截至2022年底，全省已建成具备综合功能的乡镇（街道）综合养老服务中心220个，实现了城市街道全覆盖。省市县三级累计投资6.6亿元，新增养老服务设施面积28.03万平方米，增加床位5006张，其中护理型床位3939张。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (11, '近日，河北省召开全省年度养老服务工作暨民生工程推进会，强调要健全基本养老服务体系，完善城市养老服务网络，推进县乡村三级养老服务网络建设；不折不扣扎实推进养老服务民生工程；强化养老服务安全生产和疫情防控。', '2023-04-17 20:31:08', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (12, '近日，温州市民政局印发《温州市居家养老服务机构（场所）星级评定管理办法（试行）》，进一步规范我市居家养老服务机构（场所）星级评定工作，该《管理办法》将于5月1日开始施行。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (13, '据武汉市民政局官网发布消息，经市级评估，武汉新洲区58个社区养老设施全部评定A级，其中:4A级3个、3A级48个、2A级5个、1A级2个;经区级评估，466个农村互助照料活动中心服务点联合考评，合格率100%，其中91个优秀，286个良好、89个为合格。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (14, '近日，内蒙古自治区民政厅、自治区财政厅联合印发《关于做好经认定生活不能自理经济困难老年人护理补贴发放工作的通知》，对补贴发放对象和标准、资金安排、发放时间及有关要求作出明确规定。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (15, '近日，内蒙古自治区党委副书记、主席王莉霞听取农村三级养老服务网络建设情况，并就加快推进工作落实提出具体要求，明确将“新建乡镇养老服务中心350个、村级养老服务站2000个，拓展提升200所农村互助养老幸福院服务功能”列入2023年民生实事项目。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (16, '近日，海勃湾区宜和老年公寓列入国家养老服务标准化试点项目，试点实施时间2022年12月—2024年12月，为乌海市推进养老机构标准化、规范化管理提供了充足动力，也是2020年乌海市列入全国第五批居家社区养老服务改革试点以来，推进养老服务提质增效的又一有利契机。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (17, '4月6日，广西壮族自治区民政厅表示，今年将大力推进居家和社区养老服务发展，培育十个以上老年人宜居社区，营造敬老爱老社会氛围；建设百家以上长者饭堂，让老年人享受“舌尖上的幸福”；建设千张以上家庭养老床位，把机构的专业护理服务送到家；推进万户以上适老化改造，让居家养老的老年人更安心。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (18, '4月6日，河北省政府新闻办召开“加快建设京畿福地、老有颐养的乐享河北”新闻发布会，明确今年将重点实施健全基本养老服务体系、优化普惠养老服务供给、创建协同养老示范带、创新发展养老产业、构建老年宜居环境等五个方面重点任务。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (19, '近日，贵州省民政厅官网发布《贵州省养老机构服务合同示范文本（征求意见稿）》。为进一步规范我省养老机构管理，建立公平公正、诚实守信、规范运作的养老服务市场环境，切实维护养老机构和住养老人等各方合法权益，更好地保障住养老人晚年生活，结合贵州省实际,贵州省民政厅研究起草了《贵州省养老机构服务合同示范文本（征求意见稿）》,现向社会公开征求意见。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (20, '近日，河北省发展改革委、省民政厅、省卫生健康委联合印发实施《河北省养老机构服务收费管理办法》。办法出台，旨在加强养老机构服务收费管理，进一步规范养老机构服务收费行为，维护入住老年人和养老机构的合法权益，促进河北养老服务健康发展。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (21, '近日，上海市民政局公布2022年全市养老机构服务质量日常监测结果，本轮监测覆盖全部510家正常执业的养老机构，平均得分81.5分，服务质量总体处于“良好”水平，其中128家机构获评“优秀”。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (22, '近日，天津市民政局、市市场监管委开展养老机构食品安全联合抽查，针对养老机构食品经营许可证是否合法有效、食堂食品安全管理制度的建立和落实、人员健康管理、食品原料采购和贮存等情况进行重点检查，切实保障入住养老机构的老年人身体健康和生命安全。', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (23, '近日，新疆维吾尔自治区4家单位、10名个人获全国养老服务先进表彰。据民政部公布的全国养老服务先进单位和先进个人名单，乌鲁木齐市养老福利院、洛浦县民政局中心敬老院等4个单位被授予“全国养老服务先进单位”称号，米吉提·多来提', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (24, '、付秀清等10名同志被授予“全国养老服务先进个人”称号。全国养老服务先进单位名单（新疆维吾尔自治区）：1.乌鲁木齐市养老福利院2.洛浦县民政局中心敬老院全国养老服务先进单位名单（新疆生产建设兵团）：1.新疆生产建设兵团民政局社会事务处（养老服务和儿童福利处）2.第六师五家渠市吾家乐宝养老总院全国养老服务先进个人名单（新疆维吾尔自治区）：1.米吉提·多来提（维吾尔族）叶城县中心敬老院院长2.付秀清（女）乌鲁木齐市福寿养老护理中心院长3.张慧琴（女）克拉玛依市克拉玛依区圣桥老年公寓院长4.陈茜（女）库尔勒市九九老年公寓院长5.巴扎尔古丽·夏依马尔旦（女，哈萨克族）新源县阿热勒托别镇人民政府民政办副主任兼敬老院院长全国养老服务先进个人名单（新疆生产建设兵团）：1.蔡震第十师北屯市民政局局长2.李敏慈（女）第一师阿拉尔市银龄大家庭老年公寓院长3.刘胜利第八师石河子市社会福利中心主任4.藏淑红（女）第十一师四团养老院副院长5.桂新苗（女）第十二师头屯河农场康悦养老院院长', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (25, '近日，宁夏回族自治区2家单位、5名个人获全国养老服务先进表彰。据民政部公布的全国养老服务先进单位和先进个人名单，宁夏中房养老产业发展有限公司、彭阳县中心敬老院等2个单位被授予“全国养老服务先进单位”称号，丁学芳', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (26, '、杨占平等5名同志被授予“全国养老服务先进个人”称号。全国养老服务先进单位名单（宁夏回族自治区）：1.宁夏中房养老产业发展有限公司2.彭阳县中心敬老院全国养老服务先进个人名单（宁夏回族自治区）：1.丁学芳（女，回族）宁夏回族自治区民政厅养老服务处副处长2.杨占平盐池县民政局养老服务办主任3.吴玉霞（女）宁夏邦尼老年服务中心院长4.王安祥（回族）灵武市民族综合福利院理事长5.刘静婷（女）石嘴山市大武口区医养服务中心护理部主任', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (27, '近日，青海省2家单位、5名个人获全国养老服务先进表彰。据民政部公布的全国养老服务先进单位和先进个人名单，西宁市民政局、治多县幸福敬老院等2个单位被授予“全国养老服务先进单位”称号，万玛拉旦', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (28, '、李晶等5名同志被授予“全国养老服务先进个人”称号。全国养老服务先进单位名单（青海省）：1.西宁市民政局2.治多县幸福敬老院全国养老服务先进个人名单（青海省）：1.万玛拉旦（藏族）海南藏族自治州民政局养老服务科科长2.李晶（女）民和回族土族自治县孝康养老服务中心主任3.贾文元西宁市社会福利院院长4.龙智多杰（藏族）班玛县民政局养老服务工作人员5.扎西（藏族）刚察县社会福利院院长', '2023-04-17 20:31:09', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (29, '近日，甘肃省3家单位、5名个人获全国养老服务先进表彰。据民政部公布的全国养老服务先进单位和先进个人名单，甘肃省民政厅养老服务处、平凉市老年养护院等3个单位被授予“全国养老服务先进单位”称号，周琰', '2023-04-17 20:31:10', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (30, '、李佳琪等5名同志被授予“全国养老服务先进个人”称号。全国养老服务先进单位名单（甘肃省）：1.甘肃省民政厅养老服务处2.平凉市老年养护院3.兰州美好家园养老服务有限公司全国养老服务先进个人名单（甘肃省）：1.周琰（女）张掖市民政局养老服务科科长2.李佳琪（女）酒泉市肃州区春光养老院院长3.王永先靖远县北滩中心福利院院长4.贺恒山庆阳市社会福利院院长5.袁贵清（回族）张家川回族自治县中心敬老院院长', '2023-04-17 20:31:10', NULL, NULL);
INSERT INTO `help_newsletter` VALUES (31, '近日，陕西省3家单位、6名个人获全国养老服务先进表彰。据民政部公布的全国养老服务先进单位和先进个人名单，西安市民政局养老服务和慈善事业促进处、咸阳不老帮智慧养老有限公司等3个单位被授予“全国养老服务先进单位”称号，冯华', '2023-04-17 20:31:10', NULL, NULL);
